div.script:
					$(document).ready(function() {
						
						var _animating = false;
						var currentSlide = 0;
						var currentZIndex = 1;
						var _swipeOptions = {
							swipe: swipe,
							threshold: 0
						}
												
						var MAX_SLIDES = $('.slide').size()-1;
						
						
						
						$.address.init(function($e){
							
							$('.slide').each(function(n){
								$(this).attr('data-id', n);
								$(this).append('
' + parseInt(n+1) + '/' + $('.slide').size() + '')
							});
							
							$.address.strict(false); // removes leading slash

							if ($.address.value() == '') {
				                $.address.history(false).value('title').history(true);							
				            }else{
								currentSlide = parseInt($('.slide[id="' + $.address.value() + '"]').attr('data-id'));
							}					
							
						}).change(function($e) {  
							
							goToSlide(parseInt($('.slide[id="' + $e.value + '"]').attr('data-id')));
						
						});
						
						function goToSlide($slideNum){
							_animating = true;
							currentZIndex++;
							$('.slide').eq($slideNum).css({
								'display': 'block',
								'opacity': 0,
								'z-index': currentZIndex
							}).animate({
								'opacity': 1
							}, 250, function(){
								_animating = false;
							});
						}
						
						$(document).keydown(function(e) {
							switch(e.which) {
								// user presses the left arrow
								case 37: 
									if(!_animating){
										if(currentSlide > 0) {
											currentSlide--;
											$.address.value($('.slide').eq(currentSlide).attr('id'));
										}
									}
								break;	

								// user presses the right arrow
								case 39: 
									if(!_animating){
										if(currentSlide < MAX_SLIDES){
											currentSlide++;
											$.address.value($('.slide').eq(currentSlide).attr('id'));
										}
									}
								break;

							}
						});
						
						$('.slide').swipe(_swipeOptions);
						
						function swipe($event, $direction){
							if($direction == "left"){
								if(currentSlide < MAX_SLIDES){
									currentSlide++;								
								}
							}else if($direction == "right"){
								if(currentSlide > 0){
									currentSlide--;
								}
							}
							
							$.address.value($('.slide').eq(currentSlide).attr('id'));							
							
						}
						
					});

